ALTER TABLE `consignment_status` ADD `courier_display_name` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL AFTER `display_name`;
UPDATE `consignment_status` SET `courier_display_name` = 'Accepted' WHERE `consignment_status`.`status_id` = 1; UPDATE `consignment_status` SET `courier_display_name` = 'Draft' WHERE `consignment_status`.`status_id` = 7; UPDATE `consignment_status` SET `courier_display_name` = 'Pending Acceptance' WHERE `consignment_status`.`status_id` = 8; UPDATE `consignment_status` SET `courier_display_name` = 'Getting Quotes' WHERE `consignment_status`.`status_id` = 9; UPDATE `consignment_status` SET `courier_display_name` = 'Price Approval Required' WHERE `consignment_status`.`status_id` = 10; UPDATE `consignment_status` SET `courier_display_name` = 'Collecting' WHERE `consignment_status`.`status_id` = 101; UPDATE `consignment_status` SET `courier_display_name` = 'Collected' WHERE `consignment_status`.`status_id` = 102; UPDATE `consignment_status` SET `courier_display_name` = 'In-Transit' WHERE `consignment_status`.`status_id` = 301; UPDATE `consignment_status` SET `courier_display_name` = 'In-Transit (2nd Leg)' WHERE `consignment_status`.`status_id` = 311; UPDATE `consignment_status` SET `courier_display_name` = 'In-Transit (Processed)' WHERE `consignment_status`.`status_id` = 321; UPDATE `consignment_status` SET `courier_display_name` = 'Delivered' WHERE `consignment_status`.`status_id` = 401; UPDATE `consignment_status` SET `courier_display_name` = 'Delivered (Cheque deposited)' WHERE `consignment_status`.`status_id` = 402; UPDATE `consignment_status` SET `courier_display_name` = 'Failed Delivery' WHERE `consignment_status`.`status_id` = 501; UPDATE `consignment_status` SET `courier_display_name` = 'Others' WHERE `consignment_status`.`status_id` = 601;

ALTER TABLE `consignments` ADD `inform_expire` TINYINT(2) NOT NULL COMMENT '0=no 1=expiring 2=expired' AFTER `bidding_deadline`;